
public class MathClassMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(Math.abs(-26.7));
		System.out.println(Math.ceil(7.4)); //rounds up
		System.out.println(Math.floor(7.4)); //rounds down
		System.out.println(Math.max(8.6, 5.2)); //gives the max of the 2 numbers
		System.out.println(Math.min(8.6, 5.2)); //gives the min of the 2 numbers
		System.out.println(Math.pow(5, 3)); //1stnum(5) to the power of 2ndnum(3)
		System.out.println(Math.sqrt(9)); //Square roots the number
		System.out.println(Math.round(9.4));//normally rounds
		
		System.out.println(3 /2);
	}

}
